	public static String get(int type, int signe, int day, int month, int year)
	{
		String text = "";
		long seed = ((signe + 1) * 1000000 * year * 10000 + month * 100 + day);
		switch (type)
		{
			case HORO_AMOUR:
				text = horo_amour[new Random(seed).nextInt(horo_amour.length)];
				break;
			case HORO_ARGENT:
				text = horo_argent[new Random(seed).nextInt(horo_argent.length)];
				break;
			case HORO_FAMILLE:
				text = horo_famille[new Random(seed).nextInt(horo_famille.length)];
				break;
			case HORO_SANTE:
				text = horo_sante[new Random(seed).nextInt(horo_sante.length)];
				break;
			case HORO_SOCIAL:
				text = horo_social[new Random(seed).nextInt(horo_social.length)];
				break;
			case HORO_TRAVAIL:
				text = horo_travail[new Random(seed).nextInt(horo_travail.length)];
				break;
		}
		return (text);
	}

